import{_ as t,c as r,o as d,a3 as e}from"./chunks/framework.o8T5S5EX.js";const f=JSON.parse('{"title":"Introduction","description":"","frontmatter":{"title":"Introduction","lnag":"ko","outline":"deep"},"headers":[],"relativePath":"ko/docs/introduction/index.md","filePath":"ko/docs/introduction/index.md"}'),o={name:"ko/docs/introduction/index.md"},n=e('<h1 id="rb-y1" tabindex="-1">RB-Y1 <a class="header-anchor" href="#rb-y1" aria-label="Permalink to &quot;RB-Y1&quot;">​</a></h1><p>​레인보우로보틱스가 새로 공개한 이동형 양팔로봇 RB-Y1은 한 팔당 7 자유도를 갖는 양팔과 6 자유도의 외다리를 갖춘 휴머노이드형태의 로봇으로써 이를 바퀴형 고속 모바일 플랫폼 위에 탑재하였습니다. 외팔형 협동로봇과 고정형 산업용 로봇이 가진 한계를 한 번에 해결해 다양한 산업 현장에서 반복적이고 정밀한 작업이 가능합니다.</p><p>레인보우로보틱스는 휴머노이드 로봇을 개발하면서 축적된 로봇 원천기술을 갖추고 있습니다. 이 기술을 바탕으로 생성형 AI 시대에 발맞춘 이동형 양팔로봇 RB-Y1을 개발하였습니다. 또한 다양한 AI 솔루션을 위한 개발 환경 또는 센서 등을 제공할 예정입니다.</p><h2 id="technical-specifications" tabindex="-1">Technical Specifications <a class="header-anchor" href="#technical-specifications" aria-label="Permalink to &quot;Technical Specifications&quot;">​</a></h2><h3 id="general-specifications" tabindex="-1">General Specifications <a class="header-anchor" href="#general-specifications" aria-label="Permalink to &quot;General Specifications&quot;">​</a></h3><table><thead><tr><th><strong>Attribute</strong></th><th><strong>Value</strong></th></tr></thead><tbody><tr><td><strong>크기</strong></td><td>600 x 690 x 1,400mm (W x D x H)</td></tr><tr><td><strong>배터리 용량</strong></td><td>50V, 25Ah (1,270Wh)</td></tr><tr><td><strong>자유도</strong></td><td>See <a href="#degrees-of-freedom">Degrees of Freedom</a></td></tr><tr><td><strong>중량</strong></td><td>See <a href="#weight">Weight</a></td></tr><tr><td><strong>팔 가반하중</strong></td><td>3kg (per arm)</td></tr><tr><td><strong>팔 도달범위</strong></td><td>600 mm (to wrist) + hand</td></tr><tr><td><strong>관절 구동 범위</strong></td><td>See <a href="#joint-maximum-speed-angle-range">Joint Maximum Speed, Angle Range</a></td></tr><tr><td><strong>전원 사양</strong></td><td>48 VDC</td></tr><tr><td><strong>작동 온도</strong></td><td>40°C</td></tr><tr><td><strong>안전 기능</strong></td><td>See <a href="#safety-functions">Safety Functions</a></td></tr><tr><td><strong>팔 반복 정밀도</strong></td><td>&lt; ±0.05 mm</td></tr><tr><td><strong>외관 재질</strong></td><td>Aluminum</td></tr><tr><td><strong>주행 운용 속도</strong></td><td>1.5 m/s</td></tr></tbody></table><h3 id="degrees-of-freedom" tabindex="-1">Degrees of Freedom <a class="header-anchor" href="#degrees-of-freedom" aria-label="Permalink to &quot;Degrees of Freedom&quot;">​</a></h3><table><thead><tr><th><strong>PART</strong></th><th><strong># of DOF</strong></th></tr></thead><tbody><tr><td>Arm</td><td>7 DOF x 2</td></tr><tr><td>Leg</td><td>6 DOF</td></tr><tr><td>Gripper</td><td>1 DOF x 2</td></tr><tr><td>Wheel</td><td>1 DOF x 2</td></tr><tr><td><strong>Total</strong></td><td><strong>24 DOF</strong></td></tr></tbody></table><h3 id="weight" tabindex="-1">Weight <a class="header-anchor" href="#weight" aria-label="Permalink to &quot;Weight&quot;">​</a></h3><table><thead><tr><th><strong>PART</strong></th><th><strong>WEIGHT (kg)</strong></th></tr></thead><tbody><tr><td>Upper body</td><td>38kg (Arm 11kg x 2, Torso 16kg)</td></tr><tr><td>Lower body</td><td>42kg</td></tr><tr><td>Mobile</td><td>51kg</td></tr><tr><td><strong>Total</strong></td><td><strong>131kg</strong></td></tr></tbody></table><h3 id="joint-maximum-speed-angle-range" tabindex="-1">Joint Maximum Speed, Angle Range <a class="header-anchor" href="#joint-maximum-speed-angle-range" aria-label="Permalink to &quot;Joint Maximum Speed, Angle Range&quot;">​</a></h3><table><thead><tr><th><strong>Joint</strong></th><th><strong>Speed</strong></th><th><strong>Range</strong></th></tr></thead><tbody><tr><td>Ankle roll</td><td>120°/s</td><td>-20° ~ 20°</td></tr><tr><td>Ankle pitch</td><td>120°/s</td><td>-60° ~ 70°</td></tr><tr><td>Knee</td><td>180°/s</td><td>-140° ~ 45°</td></tr><tr><td>Hip pitch</td><td>180°/s</td><td>-45° ~ 90°</td></tr><tr><td>Hip roll</td><td>180°/s</td><td>-30° ~ 30°</td></tr><tr><td>Hip yaw</td><td>180°/s</td><td>-90° ~ 90°</td></tr><tr><td>Shoulder pitch</td><td>180°/s</td><td>-135° ~ 135°</td></tr><tr><td>Shoulder roll</td><td>180°/s</td><td>0° ~ 180°</td></tr><tr><td>Shoulder yaw</td><td>180°/s</td><td>-120° ~ 120°</td></tr><tr><td>Elbow pitch</td><td>180°/s</td><td>-150° ~ 0°</td></tr><tr><td>Wrist yaw1</td><td>360°/s</td><td>-360° ~ 360°</td></tr><tr><td>Wrist pitch</td><td>360°/s</td><td>-100° ~ 115°</td></tr><tr><td>Wrist yaw2</td><td>360°/s</td><td>-360° ~ 360°</td></tr></tbody></table><h3 id="safety-functions" tabindex="-1">Safety Functions <a class="header-anchor" href="#safety-functions" aria-label="Permalink to &quot;Safety Functions&quot;">​</a></h3><ul><li><strong>Low Level Controller (Motor Controller)</strong><ul><li>Pisition Reference Continuity Error</li><li>Position Tracking Error</li><li>Temperature Error</li><li>Overcurrent Error</li><li>Communication Error</li></ul></li><li><strong>High Level Controller</strong><ul><li>Current Limit</li></ul></li></ul>',14),a=[n];function i(s,l,g,h,c,u){return d(),r("div",null,a)}const p=t(o,[["render",i]]);export{f as __pageData,p as default};
